import React, { useState } from "react";
import { SelectButton, SelectButtonChangeEvent } from 'primereact/selectbutton';

export default function PlantOptionSwitchButton() {
    const options: {label: string, value: string}[] = [
        { label: 'Demander conseil', value: 'Demander conseil' },
        { label: 'Faire garder', value: 'Faire garder' }
    ];
    const [value, setValue] = useState<string>(options[0].value);

    const customOptionTemplate = (option: {label: string, value: string}) => {
        return (
            <div className={`p-button-text ${value === option.value ? 'selected-option' : ''}`}>
                {option.label}
            </div>
        );
    };

    return (
        <div>
            <div className="flex justify-center items-center h-screen">
                <div className="bg-white rounded-full p-4 shadow-lg">
                    <SelectButton 
                        value={value} 
                        onChange={(e: SelectButtonChangeEvent) => setValue(e.value)} 
                        options={options} 
                        optionLabel="label"
                        itemTemplate={customOptionTemplate}
                    />
                </div>
            </div>
        </div>
    );
}
