import React, { useState } from "react";
import { SelectButton, SelectButtonChangeEvent } from 'primereact/selectbutton';

export default function PlantOptionSwitchButton() {
    const options: string[] = ['Demander conseil', 'Faire garder'];
    const [value, setValue] = useState<string>(options[0]);

    return (
        <div className="flex justify-center items-center h-screen">
            <div className="bg-white rounded-full p-4 shadow-lg">
                <SelectButton 
                    value={value} 
                    onChange={(e: SelectButtonChangeEvent) => setValue(e.value)} 
                    options={options} 
                    className="rounded-full p-4 appearance-none bg-transparent border-none focus:outline-none"
                    style={{
                        boxShadow: value ? '0 0 10px rgba(0, 0, 0, 0.5)' : 'none',
                        borderRadius: value ? '15px' : 'none'
                    }}
                />
            </div>
        </div>
    );
}
