// Home.jsx
import React, { useState } from 'react';
import './Home.css';
import Register from './Register';

const Home = ({ onLogin, onRegister }) => {
  const [currentPage, setCurrentPage] = useState('home');

  const renderPage = () => {
    switch (currentPage) {
      case 'login':
        return <Login />;
      case 'register':
        return <Register onClick={() => setCurrentPage('home')} />;
      default:
        return (
          <div className="homeContainer">
            <div className="logoContainer">
              <h1 className="logo">A'rosa-je</h1>
            </div>
            <div className="loginContainer">
              <h2 className="title">Bienvenue</h2>
              <p className="paragraph">Connectez-vous ou inscrivez-vous pour prendre soin de vos plantes!</p>
              <div className='buttonContainer'>
                <button className="loginButton" onClick={onLogin}>Se connecter</button>
                <button className="registerButton" onClick={() => setCurrentPage('register')}>S'inscrire</button>
              </div>
            </div>
          </div>
        );
    }
  };

  return (
    <div>
      {renderPage()}
    </div>
  );
};

export default Home;
